    function getServiceOptions(type) {
        var ajax1 = $.ajax({
            url: "/searchresult/getServices",
            type: "POST",
            data: {},
            success: function(qText) {

            }
        });

        requests.push(ajax1);

        $.when.apply($, requests).done(function() {
            var counter = 0;
            $.each(arguments, function(i, data) {
                //console.log(data);
                if (counter >= 1) {
                    return false;
                }
                var obj = (typeof data === 'object') ? JSON.parse(JSON.parse(JSON.stringify(data))[0]) : JSON.parse(data);
                if (typeof obj['list'] != "undefined") {
                    var serviceOptions = obj['list'];
                    var serviceName = [];
                    for (var i = 0; i < serviceOptions.length; i++) {
                        serviceName.push(serviceOptions[i]['display_name']);
                    }
                    //sessionStorage.setItem('serviceNames',JSON.stringify(serviceName));

                    if (type == 'M') {
                        bindSuggestermobile(serviceName);
                        $('#srch-main-mobile .suggester-input').removeClass('loading');
                        $("#srch-main-mobile .suggester-input").prop('disabled', false);
                    } else {
                        bindSuggester(serviceName);
                        $('#srch-main .suggester-input').removeClass('loading');
                        $("#srch-main .suggester-input").prop('disabled', false);
                    }
                }
                counter++;
            });
        });
    }

    /* Configure/customize these variables.*/
    var showChar = 100; // How many characters are shown by default
    var ellipsestext = "...";
    var moretext = "Show more >>>";
    var lesstext = "Show less <<<";


    $('.more').each(function() {
        var content = $(this).html();

        if (content.length > showChar) {

            var c = content.substr(0, showChar);
            var h = content.substr(showChar, content.length - showChar);

            var html = c + '<span class="moreellipses">' + ellipsestext + '&nbsp;</span><span class="morecontent"><span>' + h + '</span>&nbsp;&nbsp;<a href="" class="morelink">' + moretext + '</a></span>';

            $(this).html(html);
        }

    });

    $(".morelink").click(function() {
        if ($(this).hasClass("less")) {
            $(this).removeClass("less");
            $(this).html(moretext);
        } else {
            $(this).addClass("less");
            $(this).html(lesstext);
        }
        $(this).parent().prev().toggle();
        $(this).prev().toggle();
        return false;
    });
</script>

